mapper.select:
SELECT IFNULL(SUM(nums * purchase_price), 0) AS receivable FROM order_kuncun where store_id = #{store_id} and revokes='0'
and Date(date) >=#{date}
and Date(date) >=#{startTime} and Date(date) <=#{endTime}

SELECT
IFNULL(SUM(num * purchase_price), 0) AS receivable
FROM
order_kuncun
where store_id = #{store_id} and revokes='0'

    and Date(date) >=#{date}


    and Date(date) >=#{startTime} and Date(date) <=#{endTime}


    and Date(date) =#{date}

select * from order_kuncun where store_id = #{store_id} and revokes='0' and now_number>0

    and Date(date) >=#{date}


    and Date(date) >=#{startTime} and Date(date) <=#{endTime}


    and Date(date) =#{date}

mapper.update:
UPDATE order_pro
SET status='1',
    num=#{num},
    total=#{total}
WHERE order_pro_id=#{order_pro_id}

UPDATE order_pro
SET order_kuncun=order_kuncun-#{num}
WHERE order_pro_id=#{order_pro_id}

UPDATE order_pro
SET now_number=now_number-#{now_number}
WHERE order_pro_id=#{order_pro_id}

UPDATE order_pro
SET order_kuncun=order_kuncun+#{num}
WHERE order_pro_id=#{order_pro_id}

UPDATE order_pro
SET now_number=now_number+#{now_number}
WHERE order_pro_id=#{order_pro_id}

UPDATE order_pro
SET revokes='1'
WHERE order_info_id=#{order_info_id}

mapper.select:
SELECT op.order_pro_id, op.order_info_id, replace(op.product_name,"XINg"," ") as product_name, op.product_price, op.num, op.total, op.norms1, op.norms2, op.norms3, op.product_id, op.order_kuncun, oi.name, oi.phone, oi.total_money, oi.owe_money, oi.money, oi.discount_money, oi.date, oi.order_number, oi.status, oi.open_bill FROM order_pro op LEFT JOIN order_info oi on oi.order_info_id=op.order_info_id WHERE oi.store_id = #{store_id} and oi.revokes='0'
and Date(oi.date) >=#{startTime}
and Date(oi.date) <=#{endTime}
ORDER BY oi.date DESC

SELECT op.order_pro_id, op.order_info_id, replace(op.product_name,"XINg"," ") as product_name, op.product_price, op.num, op.total, op.norms1, op.norms2, op.norms3, op.product_id, op.order_kuncun, oi.name, oi.phone, oi.total_money, oi.owe_money, oi.money, oi.discount_money, oi.date, oi.order_number, oi.status, oi.open_bill, oi.address, s.`name` as gname FROM order_pro op LEFT JOIN order_info oi on oi.order_info_id=op.order_info_id LEFT JOIN store s on s.store_id=oi.store_id WHERE oi.revokes='0'
and oi.store_id in ( SELECT store_id FROM admin_relation where uid=#{uid} )
and oi.store_id=#{store_id}
and ( POSITION(#{name} IN oi.name) OR POSITION(#{name} IN oi.phone) )
and Date(oi.date) >=#{startTime}
and Date(oi.date) <=#{endTime}
ORDER BY oi.date DESC
