mapper.select:
SELECT p.*, s.`name`, s.gname FROM( SELECT product_id, store_id, product_name, product_price, purchase_price, production_enterprise, product_img, date, norms1, norms2, norms3, kucun, type, bar_code, bar_code_number, supplier_id, number as numbers, url, status, type2 FROM product WHERE store_id=#{store_id} and status='0' and ( getPY(product_name) like concat(concat('%',#{product_name}),'%') OR POSITION(#{product_name} IN product_name))
and type = #{type}
ORDER BY date DESC ) p LEFT JOIN supplier s on s.supplier_id=p.supplier_id

mapper.update:
UPDATE product
SET kucun=kucun-#{num}
WHERE product_id=#{product_id}

UPDATE product
SET kucun=#{kucun},likucun=#{likucun}
WHERE product_id=#{product_id}

UPDATE product
SET kucun=kucun+#{num}

        ,purchase_price=#{purchase_price}

WHERE product_id=#{product_id}

UPDATE product
SET kucun=kucun+#{num},likucun = likucun+#{now_number}
WHERE product_id=#{product_id}

UPDATE product
SET kucun=kucun+#{num},likucun = likucun+#{now_number}
WHERE product_id=#{product_id}

UPDATE product
SET kucun=#{kucun}
WHERE product_id=#{product_id}

UPDATE product
SET kucun=kucun-#{num}
WHERE product_id=#{product_id}

UPDATE product
SET likucun=likucun-#{now_number}
WHERE product_id=#{product_id}

mapper.select:
SELECT p.product_id, p.product_price, p.purchase_price, p.product_name, p.product_price * p.num AS profit, p.purchase_price * p.num AS receivable, p.num, p.product_img, p.kucun FROM ( SELECT p.product_id, p.product_price, p.purchase_price, p.product_name, IFNULL( ( SELECT SUM(op.num) FROM order_pro op LEFT JOIN order_info o ON o.order_info_id = op.order_info_id WHERE op.product_id = p.product_id AND DATE(o.date) >=#{startTime} AND DATE(o.date)<= #{endTime}
AND POSITION(#{product_name} IN `product_name`)
AND type=#{type}
GROUP BY op.product_id ),0 ) AS num, p.product_img, p.kucun, p.status FROM product p WHERE store_id = #{store_id} and status='0'
and POSITION(#{product_name} IN `product_name`)
) p

SELECT product_id, store_id, product_name, product_price, purchase_price, production_enterprise, product_img, date, norms1, norms2, norms3, kucun, type, bar_code, bar_code_number, supplier_id, number as numbers, url, status, type2 FROM product WHERE store_id=#{store_id} and status='0'
and type=#{type}
ORDER BY date DESC

mapper.update:
UPDATE product
SET supplier_id=#{supplier_id}
WHERE product_id=#{product_id}

mapper.select:
SELECT p.*, s.`name`, s.gname FROM( SELECT product_id, store_id, product_name, product_price, purchase_price, production_enterprise, product_img, date, norms1, norms2, norms3, kucun, type, bar_code, bar_code_number, supplier_id, number as numbers, url, status, type2 FROM product WHERE status='0' and store_id=#{store_id}
and supplier_id=#{supplier_id}
AND POSITION(#{name} IN `product_name`)
ORDER BY date DESC ) p LEFT JOIN supplier s on s.supplier_id=p.supplier_id

mapper.update:
UPDATE product
SET status='1'
WHERE product_id=#{product_id}

mapper.select:
SELECT date(dday) date, SUM(receivable) AS receivable, SUM(profit) AS profit FROM ( SELECT datelist AS dday, '0' AS receivable, '0' AS profit FROM calendar WHERE 1=1
and DATE(datelist) >=#{startTime}
and DATE(datelist) <=#{endTime}
UNION ALL SELECT date, IFNULL(SUM(nums * purchase_price), 0) AS receivable, IFNULL(SUM(nums * product_price), 0) AS profit FROM order_kuncun WHERE store_id = #{store_id}
and product_id =#{product_id}
and DATE(date) >=#{startTime}
and DATE(date) <=#{endTime}
) a GROUP BY date

SELECT dday AS date, SUM(receivable) AS receivable, SUM(profit) AS profit FROM ( SELECT DATE_FORMAT(datelist, '%Y-%m') AS dday, '0' AS receivable, '0' AS profit FROM calendar WHERE 1 = 1
and DATE(datelist) >=#{startTime}
and DATE(datelist)<=#{endTime}
UNION ALL SELECT DATE_FORMAT(date, '%Y-%m'), IFNULL(SUM(nums * purchase_price), 0) AS receivable, IFNULL(SUM(nums * product_price), 0) AS profit FROM order_kuncun WHERE store_id = #{store_id}
AND product_id=#{product_id}
and DATE(date) >=#{startTime}
and DATE(date) <=#{endTime}
) a GROUP BY date

        SELECT
            IFNULL(SUM(ok.nums * ok.purchase_price), 0) AS receivable,
            IFNULL(SUM(ok.nums * ok.product_price), 0) AS profit
        FROM
	      order_kuncun ok
        LEFT JOIN order_pro op on op.order_pro_id=ok.order_pro_id
        WHERE ok.store_id = #{store_id} and op.revokes='0'

            and ok.product_id=#{product_id}


            and DATE(ok.date) >=#{startTime}


            and DATE(ok.date) <=#{endTime}

SELECT p.product_price, p.product_img, p.kucun, p.product_price, p.product_id, p.product_id, p.product_name, '0' as revokes, IFNULL((SELECT SUM(ok.nums*ok.product_price) from order_kuncun ok LEFT JOIN order_pro op on op.order_pro_id=ok.order_pro_id where ok.product_id=p.product_id and op.revokes='0'),0) as profit, IFNULL((SELECT SUM(ok.nums*ok.purchase_price) from order_kuncun ok LEFT JOIN order_pro op on op.order_pro_id=ok.order_pro_id where ok.product_id=p.product_id and op.revokes='0'),0) as receivable, IFNULL((SELECT SUM(ok.nums) as saledec from order_kuncun ok LEFT JOIN order_pro op on op.order_pro_id=ok.order_pro_id where ok.product_id=p.product_id and op.revokes='0'),0) as num, IFNULL((SELECT SUM(ok.nums*ok.product_price) from order_kuncun ok LEFT JOIN order_pro op on op.order_pro_id=ok.order_pro_id where ok.product_id=p.product_id and op.revokes='0')-(SELECT SUM(ok.nums*ok.purchase_price) from order_kuncun ok LEFT JOIN order_pro op on op.order_pro_id=ok.order_pro_id where ok.product_id=p.product_id and op.revokes='0') ,0) as rec from product p WHERE p.store_id=#{store_id}
and POSITION(#{product_name} IN p.product_name)
and p.type = #{typeName}
ORDER BY num ASC
ORDER BY num DESC
ORDER BY rec ASC
ORDER BY rec DESC
limit #{pageNumber},#{pageSize}
