mapper.update:
UPDATE store
SET number=number-#{count}
where store_id=#{store_id}

UPDATE store
SET number=number+#{count}
where store_id=#{store_id}

UPDATE store
SET name=#{name}

        ,phone=#{phone}


    ,address=#{address}


    ,province=#{province}


    ,city=#{city}


    ,county=#{county}


    ,street=#{street}

where store_id=#{store_id}

UPDATE store
SET qr_code=#{qr_code}
WHERE store_id=#{store_id}

UPDATE store
SET business_img=#{business_img}
WHERE store_id=#{store_id}

UPDATE store
SET phoneTow=#{username}
WHERE store_id=#{store_id}
