mapper.insert:
INSERT INTO supplier_order_info( supplier_id, store_id, uid, order_number, money, owe_money, total_money, date, remarks, phone, name, gname, open_bill, status
,discount_money
)VALUES ( #{supplier_id}, #{store_id}, #{uid}, #{order_number}, #{money}, #{owe_money}, #{total_money}, #{date}, #{remarks}, #{phone}, #{name}, #{gname}, #{open_bill}, #{status}
,#{discount_money}
)

mapper.select:
SELECT supplier_order_info_id, supplier_id, store_id, uid, order_number, money, owe_money, total_money, date, remarks, phone, name, gname, status, revokes, open_bill FROM supplier_order_info WHERE store_id=#{pd.store_id}
AND supplier_id=#{pd.supplier_id}
and DATE(date) >=#{pd.startTime}
and DATE(date) <=#{pd.endTime}
and ( POSITION(#{pd.name} IN `name`) or POSITION(#{pd.name} IN `gname`) )
and revokes=#{pd.revokes}
ORDER BY date DESC

SELECT count(1) as count FROM supplier_order_info WHERE supplier_id=#{supplier_id} and revokes='0'

    and DATE(date) >=#{startTime}


    and DATE(date) <=#{endTime}


    and (
        POSITION(#{name} IN `name`)
        or
        POSITION(#{name} IN `gname`)
    )

SELECT count(1) as count FROM supplier_order_info WHERE store_id=#{store_id}

   and supplier_id=#{supplier_id}


    and DATE(date) >=#{startTime}


    and DATE(date) <=#{endTime}


    and (
    POSITION(#{name} IN `name`)
    or
    POSITION(#{name} IN `gname`)
    )

SELECT SUM(money) as money FROM supplier_order_info WHERE store_id=#{store_id}

    and supplier_id=#{supplier_id}


    and DATE(date) >=#{startTime}


    and DATE(date) <=#{endTime}

SELECT supplier_order_info_id, supplier_id, store_id, uid, order_number, money, owe_money, total_money, date, remarks, phone, name, gname, status, revokes, open_bill FROM supplier_order_info WHERE revokes='0'
and store_id in ( SELECT store_id FROM admin_relation where uid=#{pd.uid} )
and store_id=#{pd.store_id}
and ( POSITION(#{pd.name} IN name) OR POSITION(#{pd.name} IN gname) OR POSITION(#{pd.name} IN phone) )
and DATE(date) >=#{pd.startTime}
and DATE(date) <=#{pd.endTime}
ORDER BY date DESC
