mapper.select:
SELECT s.uid, s.username, s.password, s.salt, s.name, s.openid, s.unionid, s.status, s.store_id, s.date, s.province, s.city, s.district, s.phone, s.identity, s.address, s.person_id, s.corp, ss.name as store_name, p.NAME as person_name, ss.is_pass FROM sys_app_user s LEFT JOIN store ss on ss.store_id=s.store_id LEFT JOIN sys_user p on p.USER_ID=s.person_id where s.is_delete = 0
and s.province=#{pd.province}
and s.person_id=#{pd.USER_ID}
and s.city=#{pd.city}
and s.person_id=#{pd.person_id}
and( POSITION(#{pd.name} IN s.name) OR POSITION(#{pd.name} IN s.phone) OR POSITION(#{pd.name} IN p.name) OR POSITION(#{pd.name} IN s.address) )

mapper.update:
UPDATE sys_app_user
SET password=#{password}
where uid=#{uid}

UPDATE sys_app_user
name=#{name}, openid=#{openid}, unionid=#{unionid}, province=#{province}, city=#{city}, district=#{district}, address=#{address}, person_id=#{person_id}, phone=#{phone}, corp=#{corp}, identity=#{identity}, diedstatus =#{diedstatus }, password =#{password}, is_delete =#{is_delete},
where uid=#{uid}

UPDATE sys_app_user
SET openid=#{openid},
    unionid=#{unionid}
where uid=#{uid}

UPDATE sys_app_user
SET login_date=#{login_date}
where uid=#{uid}
