mapper.update:
UPDATE kuncun
SET num=#{num},
    money=#{money}
WHERE kuncun_id=#{kuncun_id}

UPDATE kuncun
SET nums=#{nums}
where kuncun_id=#{kuncun_id}

UPDATE  kuncun
SET nums=nums+#{nums}
where kuncun_id=#{kuncun_id}

UPDATE  kuncun
SET nums=nums-#{nums}
where kuncun_id=#{kuncun_id}

UPDATE kuncun
SET revokes='1'
WHERE order_info_id=#{supplier_order_info_id}
